package whiletest;

public class whileEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i<=10)
			System.out.print((i++)+"\t");	//"\t" tab키를 사용하는 기능

	// do {
	//	System.out.print((i++)+"\t");
	//	
	//	} while(i<=10);
	//위에꺼는 while문 참조는 do-while문이다. while문이 더 경제적	
	}

}
